package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class CustomersController {	@Autowired	private CustomersRepository repository;	@GetMapping("/customers/")	public Iterable<Customers> getCustomers() {		return repository.findAll();	}	@GetMapping(value ="/customers/{id}")	public Optional<Customers> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/customers")	public Customers create(@RequestBody Customers oneCustomers) {		return repository.save(oneCustomers);	}	@PutMapping("/customers")	public Customers update(@RequestBody Customers newCustomers) {		return repository.save(newCustomers);	}	@DeleteMapping("/customers")	public void delete(@RequestBody Customers deleteCustomers) {		repository.delete(deleteCustomers);	}}