package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class EmployeesController {	@Autowired	private EmployeesRepository repository;	@GetMapping("/employees/")	public Iterable<Employees> getEmployees() {		return repository.findAll();	}	@GetMapping(value ="/employees/{id}")	public Optional<Employees> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/employees")	public Employees create(@RequestBody Employees oneEmployees) {		return repository.save(oneEmployees);	}	@PutMapping("/employees")	public Employees update(@RequestBody Employees newEmployees) {		return repository.save(newEmployees);	}	@DeleteMapping("/employees")	public void delete(@RequestBody Employees deleteEmployees) {		repository.delete(deleteEmployees);	}}