package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class InventoryTransactionTypesController {	@Autowired	private InventoryTransactionTypesRepository repository;	@GetMapping("/inventorytransactiontypes/")	public Iterable<InventoryTransactionTypes> getInventoryTransactionTypes() {		return repository.findAll();	}	@GetMapping(value ="/inventorytransactiontypes/{id}")	public Optional<InventoryTransactionTypes> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/inventorytransactiontypes")	public InventoryTransactionTypes create(@RequestBody InventoryTransactionTypes oneInventoryTransactionTypes) {		return repository.save(oneInventoryTransactionTypes);	}	@PutMapping("/inventorytransactiontypes")	public InventoryTransactionTypes update(@RequestBody InventoryTransactionTypes newInventoryTransactionTypes) {		return repository.save(newInventoryTransactionTypes);	}	@DeleteMapping("/inventorytransactiontypes")	public void delete(@RequestBody InventoryTransactionTypes deleteInventoryTransactionTypes) {		repository.delete(deleteInventoryTransactionTypes);	}}