package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class InvoicesController {	@Autowired	private InvoicesRepository repository;	@GetMapping("/invoices/")	public Iterable<Invoices> getInvoices() {		return repository.findAll();	}	@GetMapping(value ="/invoices/{id}")	public Optional<Invoices> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/invoices")	public Invoices create(@RequestBody Invoices oneInvoices) {		return repository.save(oneInvoices);	}	@PutMapping("/invoices")	public Invoices update(@RequestBody Invoices newInvoices) {		return repository.save(newInvoices);	}	@DeleteMapping("/invoices")	public void delete(@RequestBody Invoices deleteInvoices) {		repository.delete(deleteInvoices);	}}