package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class OrderDetailsController {	@Autowired	private OrderDetailsRepository repository;	@GetMapping("/orderdetails/")	public Iterable<OrderDetails> getOrderDetails() {		return repository.findAll();	}	@GetMapping(value ="/orderdetails/{id}")	public Optional<OrderDetails> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/orderdetails")	public OrderDetails create(@RequestBody OrderDetails oneOrderDetails) {		return repository.save(oneOrderDetails);	}	@PutMapping("/orderdetails")	public OrderDetails update(@RequestBody OrderDetails newOrderDetails) {		return repository.save(newOrderDetails);	}	@DeleteMapping("/orderdetails")	public void delete(@RequestBody OrderDetails deleteOrderDetails) {		repository.delete(deleteOrderDetails);	}}