package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class OrderDetailsStatusController {	@Autowired	private OrderDetailsStatusRepository repository;	@GetMapping("/orderdetailsstatus/")	public Iterable<OrderDetailsStatus> getOrderDetailsStatus() {		return repository.findAll();	}	@GetMapping(value ="/orderdetailsstatus/{id}")	public Optional<OrderDetailsStatus> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/orderdetailsstatus")	public OrderDetailsStatus create(@RequestBody OrderDetailsStatus oneOrderDetailsStatus) {		return repository.save(oneOrderDetailsStatus);	}	@PutMapping("/orderdetailsstatus")	public OrderDetailsStatus update(@RequestBody OrderDetailsStatus newOrderDetailsStatus) {		return repository.save(newOrderDetailsStatus);	}	@DeleteMapping("/orderdetailsstatus")	public void delete(@RequestBody OrderDetailsStatus deleteOrderDetailsStatus) {		repository.delete(deleteOrderDetailsStatus);	}}