package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class OrdersController {	@Autowired	private OrdersRepository repository;	@GetMapping("/orders/")	public Iterable<Orders> getOrders() {		return repository.findAll();	}	@GetMapping(value ="/orders/{id}")	public Optional<Orders> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/orders")	public Orders create(@RequestBody Orders oneOrders) {		return repository.save(oneOrders);	}	@PutMapping("/orders")	public Orders update(@RequestBody Orders newOrders) {		return repository.save(newOrders);	}	@DeleteMapping("/orders")	public void delete(@RequestBody Orders deleteOrders) {		repository.delete(deleteOrders);	}}