package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class OrdersStatusController {	@Autowired	private OrdersStatusRepository repository;	@GetMapping("/ordersstatus/")	public Iterable<OrdersStatus> getOrdersStatus() {		return repository.findAll();	}	@GetMapping(value ="/ordersstatus/{id}")	public Optional<OrdersStatus> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/ordersstatus")	public OrdersStatus create(@RequestBody OrdersStatus oneOrdersStatus) {		return repository.save(oneOrdersStatus);	}	@PutMapping("/ordersstatus")	public OrdersStatus update(@RequestBody OrdersStatus newOrdersStatus) {		return repository.save(newOrdersStatus);	}	@DeleteMapping("/ordersstatus")	public void delete(@RequestBody OrdersStatus deleteOrdersStatus) {		repository.delete(deleteOrdersStatus);	}}