package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class OrdersTaxStatusController {	@Autowired	private OrdersTaxStatusRepository repository;	@GetMapping("/orderstaxstatus/")	public Iterable<OrdersTaxStatus> getOrdersTaxStatus() {		return repository.findAll();	}	@GetMapping(value ="/orderstaxstatus/{id}")	public Optional<OrdersTaxStatus> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/orderstaxstatus")	public OrdersTaxStatus create(@RequestBody OrdersTaxStatus oneOrdersTaxStatus) {		return repository.save(oneOrdersTaxStatus);	}	@PutMapping("/orderstaxstatus")	public OrdersTaxStatus update(@RequestBody OrdersTaxStatus newOrdersTaxStatus) {		return repository.save(newOrdersTaxStatus);	}	@DeleteMapping("/orderstaxstatus")	public void delete(@RequestBody OrdersTaxStatus deleteOrdersTaxStatus) {		repository.delete(deleteOrdersTaxStatus);	}}