package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class PrivilegesController {	@Autowired	private PrivilegesRepository repository;	@GetMapping("/privileges/")	public Iterable<Privileges> getPrivileges() {		return repository.findAll();	}	@GetMapping(value ="/privileges/{id}")	public Optional<Privileges> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/privileges")	public Privileges create(@RequestBody Privileges onePrivileges) {		return repository.save(onePrivileges);	}	@PutMapping("/privileges")	public Privileges update(@RequestBody Privileges newPrivileges) {		return repository.save(newPrivileges);	}	@DeleteMapping("/privileges")	public void delete(@RequestBody Privileges deletePrivileges) {		repository.delete(deletePrivileges);	}}