package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class PurchaseOrderStatusController {	@Autowired	private PurchaseOrderStatusRepository repository;	@GetMapping("/purchaseorderstatus/")	public Iterable<PurchaseOrderStatus> getPurchaseOrderStatus() {		return repository.findAll();	}	@GetMapping(value ="/purchaseorderstatus/{id}")	public Optional<PurchaseOrderStatus> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/purchaseorderstatus")	public PurchaseOrderStatus create(@RequestBody PurchaseOrderStatus onePurchaseOrderStatus) {		return repository.save(onePurchaseOrderStatus);	}	@PutMapping("/purchaseorderstatus")	public PurchaseOrderStatus update(@RequestBody PurchaseOrderStatus newPurchaseOrderStatus) {		return repository.save(newPurchaseOrderStatus);	}	@DeleteMapping("/purchaseorderstatus")	public void delete(@RequestBody PurchaseOrderStatus deletePurchaseOrderStatus) {		repository.delete(deletePurchaseOrderStatus);	}}