package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class PurchaseOrdersController {	@Autowired	private PurchaseOrdersRepository repository;	@GetMapping("/purchaseorders/")	public Iterable<PurchaseOrders> getPurchaseOrders() {		return repository.findAll();	}	@GetMapping(value ="/purchaseorders/{id}")	public Optional<PurchaseOrders> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/purchaseorders")	public PurchaseOrders create(@RequestBody PurchaseOrders onePurchaseOrders) {		return repository.save(onePurchaseOrders);	}	@PutMapping("/purchaseorders")	public PurchaseOrders update(@RequestBody PurchaseOrders newPurchaseOrders) {		return repository.save(newPurchaseOrders);	}	@DeleteMapping("/purchaseorders")	public void delete(@RequestBody PurchaseOrders deletePurchaseOrders) {		repository.delete(deletePurchaseOrders);	}}