package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class SalesReportsController {	@Autowired	private SalesReportsRepository repository;	@GetMapping("/salesreports/")	public Iterable<SalesReports> getSalesReports() {		return repository.findAll();	}	@GetMapping(value ="/salesreports/{id}")	public Optional<SalesReports> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/salesreports")	public SalesReports create(@RequestBody SalesReports oneSalesReports) {		return repository.save(oneSalesReports);	}	@PutMapping("/salesreports")	public SalesReports update(@RequestBody SalesReports newSalesReports) {		return repository.save(newSalesReports);	}	@DeleteMapping("/salesreports")	public void delete(@RequestBody SalesReports deleteSalesReports) {		repository.delete(deleteSalesReports);	}}