package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class ShippersController {	@Autowired	private ShippersRepository repository;	@GetMapping("/shippers/")	public Iterable<Shippers> getShippers() {		return repository.findAll();	}	@GetMapping(value ="/shippers/{id}")	public Optional<Shippers> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/shippers")	public Shippers create(@RequestBody Shippers oneShippers) {		return repository.save(oneShippers);	}	@PutMapping("/shippers")	public Shippers update(@RequestBody Shippers newShippers) {		return repository.save(newShippers);	}	@DeleteMapping("/shippers")	public void delete(@RequestBody Shippers deleteShippers) {		repository.delete(deleteShippers);	}}