package fi.vamk.e1900281.finalProject;import java.util.*;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.DeleteMapping;import org.springframework.web.bind.annotation.PutMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RestController;
@RestControllerpublic class StringsController {	@Autowired	private StringsRepository repository;	@GetMapping("/strings/")	public Iterable<Strings> getStrings() {		return repository.findAll();	}	@GetMapping(value ="/strings/{id}")	public Optional<Strings> getById(@PathVariable("id") int id) {		return repository.findById(id);	}	@PostMapping("/strings")	public Strings create(@RequestBody Strings oneStrings) {		return repository.save(oneStrings);	}	@PutMapping("/strings")	public Strings update(@RequestBody Strings newStrings) {		return repository.save(newStrings);	}	@DeleteMapping("/strings")	public void delete(@RequestBody Strings deleteStrings) {		repository.delete(deleteStrings);	}}